    var b = 0;
    var bullets = [];
    var lastBullet = 0;
    function init(e) {
      var fire, forget, blank = null;
      var bulletEls = document.querySelectorAll("[data-fire]");
      for (var i = 0; i < bulletEls.length; ++i) {
        var eachBullet = bulletEls[i];

        if ("text" == eachBullet.localName ) {
          wrapText(eachBullet);
        }
        fire = +eachBullet.getAttribute("data-fire");
        forget = +eachBullet.getAttribute("data-forget");
        blank = eachBullet.getAttribute("data-blank");


        if (lastBullet < fire) {
          lastBullet = fire;
        }

        // TODO: allow multiple fire/forget values, comma-separated, 
        // to allow a single item to show and hide more than once
        eachBullet.addEventListener( "next" + fire, show, false);
        eachBullet.addEventListener( "back" + (fire - 1), hide, false);

        if ( null != forget ) {
          eachBullet.addEventListener( "next" + forget, hide, false);
          eachBullet.addEventListener( "back" + (forget - 1), show, false);
        }

        if ( blank && "true" == blank ) {
          eachBullet.style.display = "none";
          eachBullet.addEventListener( "back0", hide, false);
        }
        else {
          eachBullet.addEventListener( "back0", show, false);
        }

        bullets.push(eachBullet);
      }
    }

    document.onkeydown = function(e) {
      var keyCode = null;
      if (!e) {
        e = window.event;        
      }
      e.preventDefault();

      if (window.event) {
        keyCode = window.event.keyCode;
      } else if (e.which) {
        keyCode = e.which;
      }
      else {
        return true;
      }

      if (37 == keyCode || 39 == keyCode) {
        // changeSlide( keyCode );
      } else if (40 == keyCode) {
        b++;
        if (lastBullet < b) {
          b = lastBullet;
        } else {
          sendEvent("next");          
        }
      } else if (38 == keyCode) {
        b--;
        if (0 > b) {
          b = 0;
        } else {
          sendEvent("back");          
        }
      }
    }

    function sendEvent( dir ) {
    	if (window.CustomEvent) {
    		var bulletEvent = new CustomEvent( dir + b, {
    			detail: {
    				message: dir + b,
    				time: new Date(),
    			},
    			bubbles: false,
    			cancelable: true
    		});

    		if (!dir) {
    		  dir = "next";
    		}

        for (var i = 0; i < bullets.length; ++i) {
          if ( 1 == b && "next" == dir) {
            bullets[i].style.display = "none";
          }

          bullets[i].dispatchEvent(bulletEvent);            
        }        
    	}
    }

    function show(e) {
      e.currentTarget.style.display = "inline";
    }

    function hide(e) {
      e.currentTarget.style.display = "none";
    }

    function wrapText(el) {
      var width = +el.getAttribute("width");
      if ( !width ) {
        width = parseFloat(el.style.width);
      }
      if ( width ) {
        var words = el.textContent;
        if ( words ) {
          var words = words.trim().replace("\s\s/gi", " ").split(' ');
          el.textContent = "";
          var x = el.getAttribute("x");
          if ( !x ) {
            x= 0;
          }

          var tspanEl = document.createElementNS("http://www.w3.org/2000/svg", "tspan");
          tspanEl.setAttribute( "x", x);
          tspanEl.appendChild( document.createTextNode( words[0] ) );
          el.appendChild( tspanEl );

          for (var w = 1; w < words.length; ++w ) {
            var eachWord = words[w];
            var len = tspanEl.textContent.length;
            tspanEl.textContent += " " + eachWord;

            if (tspanEl.getComputedTextLength() > width) {
              tspanEl.textContent = tspanEl.textContent.slice(0, len);

              var tspanEl = tspanEl.cloneNode();
              tspanEl.setAttribute( "dy", "1.2em");
              tspanEl.appendChild( document.createTextNode( words[w] ) );
              el.appendChild( tspanEl );
            }
          }
        }
      }                      
    }






































































































































































